		SET grpaddrid = #{grpaddrid}
		  , doroaddrid = #{doroaddrid}
		  , jibunaddrid = #{jibunaddrid}
		  , addrlcantnm = #{addrlcantnm}
 		  , addrbasc = #{addrbasc}
 		  , addrdtl = #{addrdtl}
 		  , cnts = #{cnts}
