mapper.select:

/* ProdMapper.selectOrdersMapping */
	SELECT m_ordercd
	  FROM comm_orders
	 WHERE ordercd = #{ordercd}::TEXT
	  AND sitename = #{sitename}


/* ProdMapper.selectQnaQuestionDetail */
