	  FROM comm_qna_products_question
	 WHERE api_id = #{api_id}::TEXT
	   AND sitename = #{sitename}

mapper.insert:
/* ProdMapper.insertQnaQuestion */
	INSERT INTO comm_qna_products_question  
	(
		  subject
		, contents
		, productcd
		, m_productcd
		, ordercd
		, m_ordercd
		, username
		, qnatype
		, api_id
		, api_seq
		, sitename
		, regdate
		, apiindexing
		, indexingdate
	) VALUES (
		  #{subject}
		, #{contents}
		, #{productno}
		, #{m_productcd}
		, #{ordercd}
		, #{m_ordercd}
		, #{username}
		, #{qnatype}
		, #{api_id}
		, #{api_seq}
		, #{sitename}
		, now()
		, 'N'
		, now()
	)

mapper.update:
/* ProdMapper.updateQnaQuestion */
	UPDATE comm_qna_products_question
		SET subject = #{subject}
		, contents = #{contents}
		, productcd = #{productno}
		, m_productcd = #{m_productcd}
		, ordercd = #{ordercd}
		, m_ordercd = #{m_ordercd}
		, username = #{username}
		, qnatype = #{qnatype}
		, api_seq = ''
		, sitename = #{sitename}
		, indexingdate = NOW()
	WHERE api_id = #{api_id}::TEXT
	AND sitename = #{sitename}

mapper.select:

/* ProdMapper.selectQnaAnswerList */
	SELECT *
	  FROM comm_qna_products_answer
	 WHERE apiindexing = 'N'
	   AND sitename = #{sitename}

		/* ProdMapper.selectCsQnaAnswerList */
		SELECT *
		FROM comm_qna_products_answer
		WHERE apiindexing = 'N'
		  AND sitename = #{sitename}
          AND coalesce(api_seq,'') != ''

mapper.update:
/* ProdMapper.updateQnaAnswer */
	UPDATE comm_qna_products_answer
		SET indexingdate = NOW()
		, apiindexing = 'Y'
	WHERE api_id = #{api_id}::TEXT
	AND sitename = #{sitename}
